        <script>

    const PRECISION = 10_0000;
                                                                      
    
    const amountInput = document.getElementById('select_amount_from');                   
    const amountOutput = document.getElementById('select_amount_to');        
    const currencyFromSelect = document.getElementById('select_currency_from');      
    const currencyToSelect = document.getElementById('select_currency_to'); 

      
    function convertAmount() {          
      const amount = amountInput.value;                                
      const currencyFrom = currencyFromSelect.value;
      const currencyTo = currencyToSelect.value;
    
      if (currencyFrom === currencyTo){                                    
        amountOutput.value = amount;
        return;                                                    
      }
      if(currencyFrom != currencyTo){
 
        let xhr = new XMLHttpRequest();

        xhr.open('GET', 'https://min-api.cryptocompare.com/data/price?fsym='+currencyFrom+'&tsyms='+currencyTo+'&api_key=6188f83e258222449c2aa315d755bc5cb7fb710833a3a3452160c1efed357417');

        xhr.responseType = 'json';

        xhr.send();

        xhr.onload = function() {
            let responseObj = xhr.response;                                                                
           const conversion = amount * responseObj[currencyTo];  
                                    
            amountOutput.value = Math.round((conversion*1.07) * PRECISION)/ PRECISION;       
        };
    }}
          
    </script>
                 
